mapper.insert:
update ims_supplier
set supplier_name = #{supplier.supplierName},fk_create_user = #{supplier.createUser},
    create_time = #{supplier.createTime},fk_update_user = #{supplier.updateUser},update_time = #{supplier.updateTime},
    is_deleted = #{supplier.isDeleted}, status = #{supplier.status}
where
    id = #{supplier.id}
